  display: -ms-flexbox;
  display: flex;
  -webkit-box-align: center;
  -ms-flex-align: center;
  align-items: center;
}

.contact-details__info li .icon {
  height: 80px;
  width: 80px;
  background-color: var(--theme-color2);
  display: -webkit-box;
  display: -ms-flexbox;
  display: flex;
  -webkit-box-align: center;
  -ms-flex-align: center;
  align-items: center;
  -webkit-box-pack: center;
  -ms-flex-pack: center;
  justify-content: center;
  -webkit-transition: all 500ms ease;
  transition: all 500ms ease;
}

.contact-details__info li .icon span {
  color: var(--text-color-bg-theme-color2);
  font-size: 25px;
  -webkit-transition: all 500ms ease;
  transition: all 500ms ease;
}

.contact-details__info li:hover .icon {
  background-color: var(--theme-color1);
}

.contact-details__info li:hover .icon span {
  color: var(--text-color-bg-theme-color1);
}

.contact-details__info li .text {
  margin-left: 30px;
}

.contact-details__info li .text p {
  font-size: 14px;
  line-height: 24px;
}

.contact-details__info li .text a {
  font-size: 18px;
  -webkit-transition: all 500ms ease;
  transition: all 500ms ease;
}

.contact-details__info li .text span {
  font-size: 20px;
}

.contact-details__info li+li {
  margin-top: 30px;
}

/***

==================================================================
	Main Footer
==================================================================

***/
.main-footer {
  position: relative;
  background-color: var(--theme-color2);
  /* Widget Section */
}

.main-footer .footer-upper {
  position: relative;
  padding: 30px 0 7px;
  background-color: #151518;
}

.main-footer .widgets-section {
  position: relative;
  padding: 70px 0 50px;


  /*new*/
  background-color: #ffffff;

}

.main-footer .footer-column {
  position: relative;
  margin-bottom: 50px;
}

.main-footer .footer-widget {
  position: relative;
}

.main-footer .widget-title {
  position: relative;
  font-weight: 600;
  color: #ffffff;
  line-height: 1.2em;
  margin-bottom: 20px;
}

.contact-info-block {
  position: relative;
  margin-bottom: 30px;
}

.contact-info-block:last-child .inner:before {
  display: none;
}

.contact-info-block .inner {
  position: relative;
}

.contact-info-block .inner:before {
  position: absolute;
  right: 20px;
  top: 0;
  height: 100%;
  width: 1px;
  background-color: #fff;
  opacity: 0.1;
  content: "";
  display: none;
}

@media (max-width: 767.98px) {
  .contact-info-block .inner {
    margin-bottom: 30px;
    padding-left: 100px;
    text-align: left;
  }
}

@media (max-width: 575.98px) {
  .contact-info-block .inner {
    margin-bottom: 0;
    padding-left: 0;
    text-align: center;
  }
}

.contact-info-block .inner .sub-title {
  color: #7f7f7f;
  display: block;
  font-size: 12px;
  font-weight: 400;
  line-height: 20px;
  margin: 10px 0 5px;
}

@media (max-width: 767.98px) {
  .contact-info-block .inner .sub-title {
    margin: 0;
  }
}

.contact-info-block .inner .text {
  font-size: 18px;
  color: #fff;
  line-height: 24px;
}

.contact-info-block .inner .text a {
  color: #fff;
}

.contact-info-block .inner .text a:hover {
  color: var(--theme-color1);
}

.main-footer .about-widget {
  position: relative;
}

.main-footer .about-widget .logo {
  margin-bottom: 15px;
  width: 152px;
}

.main-footer .about-widget .text {
  margin-bottom: 30px;

  /*new*/
  color: #000000;
}

/*=== User LInks ===*/
.user-links {
  position: relative;
}

.user-links li {
  position: relative;
  font-size: 15px;
  line-height: 26px;
  /* color: #75767a; */
  color: #000000;
  font-weight: 400;
  margin-bottom: 10px;
}

.user-links li:last-child {
  margin-bottom: 0;
}

.user-links li a {
  position: relative;
  display: inline-block;
  color: inherit;
  -webkit-transition: all 300ms ease;
  transition: all 300ms ease;
}

.user-links li a:hover {
  /* color: #FFFFFF; */
  color: #000000;
}

.user-links li a:before {
  position: absolute;
  left: 0;
  bottom: 0;
  width: 0;
  height: 1px;
  background-color: var(--bg-theme-color1);
  content: "";
  -webkit-transition: all 300ms ease;
  transition: all 300ms ease;
}

.user-links li a:hover:before {
  width: 100%;
}

.user-links.two-column {
  display: -webkit-box;
  display: -ms-flexbox;
  display: flex;
  -ms-flex-wrap: wrap;
  flex-wrap: wrap;
}

.user-links.two-column li {
  width: 50%;
  -webkit-box-flex: 0;
  -ms-flex: 0 0 50%;
  flex: 0 0 50%;
}

.newsletter-widget {
  position: relative;
}

.newsletter-widget .text {
  margin-bottom: 10px;
}

/* Subscribe Form */
.subscribe-form {
  position: relative;
}

.subscribe-form .form-group {
  position: relative;
}

.subscribe-form .form-group input[type=text],
.subscribe-form .form-group input[type=email] {
  position: relative;
  display: block;
  height: 60px;
  width: 100%;
  font-size: 14px;
  line-height: 30px;
  color: #717070;
  padding: 20px 30px;
  background: #fff;
  text-align: center;
  -webkit-transition: all 300ms ease;
  transition: all 300ms ease;
}

.subscribe-form .form-group input[type=text]::-webkit-input-placeholder,
.subscribe-form .form-group input[type=email]::-webkit-input-placeholder {
  color: #717070;
}

.subscribe-form .form-group input[type=text]::-moz-placeholder,
.subscribe-form .form-group input[type=email]::-moz-placeholder {
  color: #717070;
}

.subscribe-form .form-group input[type=text]:-ms-input-placeholder,
.subscribe-form .form-group input[type=email]:-ms-input-placeholder {
  color: #717070;
}

.subscribe-form .form-group input[type=text]::-ms-input-placeholder,
.subscribe-form .form-group input[type=email]::-ms-input-placeholder {
  color: #717070;
}

.subscribe-form .form-group input[type=text]::placeholder,
.subscribe-form .form-group input[type=email]::placeholder {
  color: #717070;
}

.subscribe-form .form-group .theme-btn {
  position: relative;
  margin-top: 10px;
  width: 100%;
}

/*=== Footer Bottom ===*/
.footer-bottom {
  position: relative;
  z-index: 3;
  text-align: center;
}

.footer-bottom .copyright-text {
  position: relative;
  font-size: 15px;
  line-height: 24px;
  padding: 33px 15px;
  font-weight: 400;
  /* color: #75767a; */
  color: #ffffff;
  margin-bottom: 0;
  border-top: 1px solid rgba(255, 255, 255, 0.1);
}

.footer-bottom .copyright-text a {
  color: inherit;
}

.footer-bottom .copyright-text a:hover {
  color: #ffffff;
}

